GO.bad_example:
func StartWorker() {
    go func() {
        for {
            doWork()
            // no exit condition, no tracking
        }
    }()
}

GO.good_example:
func StartWorker(ctx context.Context, wg *sync.WaitGroup) {
    wg.Add(1)
    go func() {
        defer wg.Done()
        for {
            select {
            case <-ctx.Done():
                return // exit when signaled
            default:
                doWork()
            }
        }
    }()
}

// Now the caller can cancel the context or wait on the WaitGroup to ensure the goroutine stops.

